                                                                                                                                                                                                                using System;

                                                                                                                                                                                                                namespace Calculator
                                                                                                                                                                                                                {
                                                                                                                                                                                                                    class Program
                                                                                                                                                                                                                        {
                                                                                                                                                                                                                                static void Main(string[] args)
                                                                                                                                                                                                                                        {
                                                                                                                                                                                                                                                    Calculator calculator = new Calculator();
                                                                                                                                                                                                                                                                Console.WriteLine("Simple Calculator");
                                                                                                                                                                                                                                                                            Console.WriteLine("1. Addition");
                                                                                                                                                                                                                                                                                        Console.WriteLine("2. Subtraction");
                                                                                                                                                                                                                                                                                                    Console.WriteLine("3. Multiplication");
                                                                                                                                                                                                                                                                                                                Console.WriteLine("4. Division");
                                                                                                                                                                                                                                                                                                                            Console.Write("Enter your choice (1/2/3/4): ");
                                                                                                                                                                                                                                                                                                                                        int choice = Convert.ToInt32(Console.ReadLine());
                                                                                                                                                                                                                                                                                                                                                    Console.Write("Enter first number: ");
                                                                                                                                                                                                                                                                                                                                                                double num1 = Convert.ToDouble(Console.ReadLine());
                                                                                                                                                                                                                                                                                                                                                                            Console.Write("Enter second number: ");
                                                                                                                                                                                                                                                                                                                                                                                        double num2 = Convert.ToDouble(Console.ReadLine());

                                                                                                                                                                                                                                                                                                                                                                                                    switch (choice)
                                                                                                                                                                                                                                                                                                                                                                                                                {
                                                                                                                                                                                                                                                                                                                                                                                                                                case 1:
                                                                                                                                                                                                                                                                                                                                                                                                                                                    Console.WriteLine($"Result: {num1} + {num2} = {calculator.Add(num1, num2)}");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        break;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        case 2:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            Console.WriteLine($"Result: {num1} - {num2} = {calculator.Subtract(num1, num2)}");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                break;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                case 3:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    Console.WriteLine($"Result: {num1} * {num2} = {calculator.Multiply(num1, num2)}");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        break;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        case 4:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            try
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        Console.WriteLine($"Result: {num1} / {num2} = {calculator.Divide(num1, num2)}");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                catch (DivideByZeroException ex)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            Console.WriteLine(ex.Message);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    break;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    default:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        Console.WriteLine("Invalid choice");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            break;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }
